import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
    Typography,
    Box,
    Radio,
    RadioGroup,
    FormControlLabel,
    FormControl,
    CircularProgress,
    Alert
} from '@mui/material';
import { useFormContext } from 'react-hook-form';

const VehicleTypeStep = () => {
    const [types, setTypes] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const { watch, setValue } = useFormContext();
    const wheels = watch('wheels');

    useEffect(() => {
        const fetchTypes = async () => {
            if (!wheels) {
                setTypes([]);
                return;
            }

            setLoading(true);
            setError(null);

            try {
                const response = await axios.get(`/api/vehicle-types?wheels=${wheels}`);
                setTypes(response.data);
            } catch (err) {
                console.error('API Error:', err);
                setError('Failed to load vehicle types. Please try again.');
                setTypes([]);
            } finally {
                setLoading(false);
            }
        };

        fetchTypes();
    }, [wheels]);

    return (
        <Box sx={{ p: 2 }}>
            <Typography variant="h5" gutterBottom>
                Select Vehicle Type ({wheels ? `${wheels}-wheel vehicles` : 'Please select number of wheels first'})
            </Typography>

            {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}

            <FormControl component="fieldset" fullWidth disabled={!wheels}>
                {loading ? (
                    <Box display="flex" justifyContent="center">
                        <CircularProgress />
                    </Box>
                ) : types.length > 0 ? (
                    <RadioGroup
                        name="vehicleType"
                        onChange={(e) => setValue('vehicleType', parseInt(e.target.value))}
                    >
                        {types.map(type => (
                            <FormControlLabel
                                key={type.id}
                                value={type.id}
                                control={<Radio />}
                                label={`${type.name} (${type.vehicles?.length || 0} available)`}
                            />
                        ))}
                    </RadioGroup>
                ) : (
                    <Typography variant="body1" color="text.secondary">
                        {wheels ? `No ${wheels}-wheel vehicle types found` : 'Please select number of wheels first'}
                    </Typography>
                )}
            </FormControl>
        </Box>
    );
};

export default VehicleTypeStep;
