import React, { useEffect, useState } from 'react';
import { getVehicleTypes } from '../api';
import {
  Button,
  Typography,
  Box,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormControl,
  CircularProgress
} from '@mui/material';

const VehicleTypeStep = ({ formData, setFormData, nextStep, prevStep }) => {
  const [types, setTypes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (formData.wheels) {
      setLoading(true);
      getVehicleTypes(formData.wheels)
        .then(response => {
          setTypes(response.data);
          setLoading(false);
        })
        .catch(err => {
          console.error('Error:', err);
          setError('Failed to load vehicle types');
          setLoading(false);
        });
    }
  }, [formData.wheels]);

  const handleTypeChange = (e) => {
    setFormData(prev => ({ ...prev, vehicleType: e.target.value }));
  };

  const handleNext = (e) => {
    e.preventDefault();
    if (formData.vehicleType) {
      nextStep();
    }
  };

  return (
    <Box component="form" onSubmit={handleNext} sx={{ p: 2 }}>
      <Typography variant="h5" gutterBottom>
        Select Vehicle Type ({formData.wheels}-wheel vehicles)
      </Typography>

      {error && (
        <Typography color="error" sx={{ mb: 2 }}>
          {error}
        </Typography>
      )}

      {loading ? (
        <Box display="flex" justifyContent="center" sx={{ my: 4 }}>
          <CircularProgress />
        </Box>
      ) : types.length > 0 ? (
        <FormControl component="fieldset" fullWidth>
          <RadioGroup
            name="vehicleType"
            value={formData.vehicleType || ''}
            onChange={handleTypeChange}
          >
            {types.map(type => (
              <FormControlLabel
                key={type.id}
                value={type.id}
                control={<Radio />}
                label={`${type.name}`}
              />
            ))}
          </RadioGroup>
        </FormControl>
      ) : (
        <Typography color="text.secondary">
          No vehicle types found for {formData.wheels}-wheel vehicles
        </Typography>
      )}

      <Box mt={4} display="flex" justifyContent="space-between">
        <Button variant="outlined" onClick={prevStep}>
          Back
        </Button>
        <Button
          type="submit"
          variant="contained"
          disabled={!formData.vehicleType}
        >
          Next
        </Button>
      </Box>
    </Box>
  );
};

export default VehicleTypeStep;
