import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { Stepper, Step, StepLabel, Button, Box, Container } from '@mui/material';
import axios from 'axios';
import NameStep from '../components/NameStep';
import WheelsStep from '../components/WheelsStep';
import VehicleTypeStep from '../components/VehicleTypeStep';
import VehicleStep from '../components/VehicleStep';
import DateStep from '../components/DateStep';

const steps = ['Name', 'Wheels', 'Vehicle Type', 'Vehicle Model', 'Dates'];

const BookingForm = () => {
  const [activeStep, setActiveStep] = useState(0);
  const { register, handleSubmit, formState: { errors }, watch, setValue, trigger } = useForm();

  const handleNext = async () => {
    const isValid = await trigger();
    if (isValid) setActiveStep(prev => prev + 1);
  };

  const onSubmit = async (data) => {
    await axios.post('http://localhost:5000/api/bookings', {
      userId: 1, // Hardcoded for demo
      vehicleId: data.vehicle,
      startDate: data.startDate,
      endDate: data.endDate
    });
  };

  return (
    <Container maxWidth="md">
      <Stepper activeStep={activeStep}>
        {steps.map(label => <Step key={label}><StepLabel>{label}</StepLabel></Step>)}
      </Stepper>

      <form onSubmit={handleSubmit(onSubmit)}>
        {activeStep === 0 && <NameStep register={register} errors={errors} />}
        {activeStep === 1 && <WheelsStep register={register} errors={errors} setValue={setValue} />}
        {activeStep === 2 && <VehicleTypeStep register={register} errors={errors} watch={watch} setValue={setValue} />}
        {activeStep === 3 && <VehicleStep register={register} errors={errors} watch={watch} setValue={setValue} />}
        {activeStep === 4 && <DateStep register={register} errors={errors} watch={watch} setValue={setValue} />}

        <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 2 }}>
          <Button disabled={activeStep === 0} onClick={() => setActiveStep(prev => prev - 1)}>
            Back
          </Button>
          {activeStep === steps.length - 1 ? (
            <Button type="submit" variant="contained">
              Submit
            </Button>
          ) : (
            <Button variant="contained" onClick={handleNext}>
              Next
            </Button>
          )}
        </Box>
      </form>
    </Container>
  );
};

export default BookingForm;
