import React, { useEffect, useState } from 'react';
import { getVehicleTypes } from '../api';
import {
  Button,
  Typography,
  Box,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormControl,
  CircularProgress,
  Alert
} from '@mui/material';

const VehicleTypeStep = ({ formData = {}, handleChange, nextStep, prevStep }) => {
  const [types, setTypes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTypes = async () => {
      // Check if wheels is properly set
      if (!formData?.wheels || isNaN(formData.wheels)) {
        setTypes([]);
        return;
      }

      setLoading(true);
      setError(null);

      try {
        const response = await getVehicleTypes(formData.wheels);
        // API returns array of types with nested Vehicles
        setTypes(response.data);
      } catch (err) {
        console.error('API Error:', err);
        setError('Failed to load vehicle types. Please try again.');
        setTypes([]);
      } finally {
        setLoading(false);
      }
    };

    fetchTypes();
  }, [formData?.wheels]);

  const handleNext = (e) => {
    e.preventDefault();
    if (formData?.vehicleType) {
      nextStep();
    }
  };

  return (
    <Box sx={{ p: 2 }}>
      <Typography variant="h5" gutterBottom>
        Select Vehicle Type ({formData?.wheels}-wheel vehicles)
      </Typography>

      {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}

      <form onSubmit={handleNext}>
        <FormControl component="fieldset" fullWidth>
          {loading ? (
            <Box display="flex" justifyContent="center">
              <CircularProgress />
            </Box>
          ) : types.length > 0 ? (
            <RadioGroup
              name="vehicleType"
              value={formData?.vehicleType || ''}
              onChange={handleChange('vehicleType')}
            >
              {types.map(type => (
                <FormControlLabel
                  key={type.id}
                  value={type.id.toString()}
                  control={<Radio />}
                  label={`${type.name} (${type.Vehicles?.length || 0} available)`}
                />
              ))}
            </RadioGroup>
          ) : (
            <Typography variant="body1" color="text.secondary">
              {formData?.wheels 
                ? `No ${formData.wheels}-wheel vehicle types found`
                : 'Please select number of wheels first'}
            </Typography>
          )}
        </FormControl>

        <Box mt={4} display="flex" justifyContent="space-between">
          <Button variant="outlined" onClick={prevStep}>
            Back
          </Button>
          <Button
            type="submit"
            variant="contained"
            disabled={!formData?.vehicleType || loading}
          >
            Next
          </Button>
        </Box>
      </form>
    </Box>
  );
};

export default VehicleTypeStep;
