import React, { useEffect, useState } from 'react';
import { getVehicleTypes } from '../api.js';
import {
  Button,
  Typography,
  Box,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormControl,
  CircularProgress
} from '@mui/material';

const VehicleTypeStep = ({ formData = {}, handleChange, nextStep, prevStep }) => {
  const [types, setTypes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchTypes = async () => {
      try {
        if (!formData?.wheels) {  // Changed from wheelCount to wheels
          setLoading(false);
          return;
        }

        // Pass wheels parameter to API call
        const response = await getVehicleTypes(formData.wheels);
        setTypes(response.data);  // No need to filter - backend handles it
        setLoading(false);
      } catch (error) {
        console.error('Error fetching vehicle types:', error);
        setLoading(false);
      }
    };

    fetchTypes();
  }, [formData?.wheels]);  // Changed dependency to wheels

  const handleNext = (e) => {
    e.preventDefault();
    if (formData?.vehicleType) {
      nextStep();
    }
  };

  return (
    <Box>
      <Typography variant="h4" gutterBottom>Type of vehicle</Typography>
      <form onSubmit={handleNext}>
        {loading ? (
          <CircularProgress />
        ) : types.length > 0 ? (
          <FormControl component="fieldset">
            <RadioGroup
              aria-label="vehicleType"
              name="vehicleType"
              value={formData?.vehicleType || ''}
              onChange={handleChange('vehicleType')}
            >
              {types.map(type => (
                <FormControlLabel 
                  key={type.id} 
                  value={type.id.toString()} 
                  control={<Radio />} 
                  label={type.name} 
                />
              ))}
            </RadioGroup>
          </FormControl>
        ) : (
          <Typography>No vehicle types found for {formData?.wheels}-wheel vehicles</Typography>
        )}
        <Box mt={2} display="flex" justifyContent="space-between">
          <Button variant="outlined" onClick={prevStep}>Back</Button>
          <Button 
            type="submit" 
            variant="contained" 
            color="primary" 
            disabled={!formData?.vehicleType || loading}
          >
            Next
          </Button>
        </Box>
      </form>
    </Box>
  );
};

export default VehicleTypeStep;
