import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
    Typography,
    Box,
    Radio,
    RadioGroup,
    FormControlLabel,
    FormControl,
    CircularProgress,
    Alert,
    Button
} from '@mui/material';

const VehicleTypeStep = ({ wheels, value, onChange, onNext, onBack }) => {
    const [types, setTypes] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchTypes = async () => {
            if (!wheels) {
                setTypes([]);
                return;
            }

            setLoading(true);
            setError(null);

            try {
                const response = await axios.get(`/api/vehicle-types?wheels=${wheels}`);
                setTypes(response.data);
            } catch (err) {
                console.error('API Error:', err);
                setError('Failed to load vehicle types. Please try again.');
                setTypes([]);
            } finally {
                setLoading(false);
            }
        };

        fetchTypes();
    }, [wheels]);

    const handleSubmit = (e) => {
        e.preventDefault();
        if (value) {
            onNext();
        }
    };

    return (
        <Box component="form" onSubmit={handleSubmit} sx={{ p: 2 }}>
            <Typography variant="h5" gutterBottom>
                Select Vehicle Type ({wheels ? `${wheels}-wheel vehicles` : 'Please go back and select number of wheels first'})
            </Typography>

            {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}

            <FormControl component="fieldset" fullWidth disabled={!wheels} sx={{ mt: 2 }}>
                {loading ? (
                    <Box display="flex" justifyContent="center">
                        <CircularProgress />
                    </Box>
                ) : types.length > 0 ? (
                    <RadioGroup
                        name="vehicleType"
                        value={value || ''}
                        onChange={(e) => onChange(parseInt(e.target.value))}
                    >
                        {types.map(type => (
                            <FormControlLabel
                                key={type.id}
                                value={type.id}
                                control={<Radio />}
                                label={`${type.name} (${type.vehicles?.length || 0} available)`}
                            />
                        ))}
                    </RadioGroup>
                ) : (
                    <Typography variant="body1" color="text.secondary">
                        {wheels ? `No ${wheels}-wheel vehicle types found` : 'Please go back and select number of wheels first'}
                    </Typography>
                )}
            </FormControl>

            <Box mt={4} display="flex" justifyContent="space-between">
                <Button onClick={onBack}>Back</Button>
                <Button
                    type="submit"
                    variant="contained"
                    disabled={!value || loading}
                >
                    Next
                </Button>
            </Box>
        </Box>
    );
};

export default VehicleTypeStep;
