import React, { useState } from "react";
import { useForm, FormProvider } from "react-hook-form";
import { Stepper, Step, StepLabel, Button } from "@mui/material";
import NameStep from "./steps/NameStep";
import WheelsStep from "./steps/WheelsStep";
import VehicleTypeStep from "./steps/VehicleTypeStep";
import VehicleModelStep from "./steps/VehicleModelStep";
import DatesStep from "./steps/DatesStep";

const steps = ["Name", "Wheels", "Vehicle Type", "Vehicle Model", "Dates"];
const StepComponents = [NameStep, WheelsStep, VehicleTypeStep, VehicleModelStep, DatesStep];

const BookingForm = () => {
  const [activeStep, setActiveStep] = useState(0);
  const methods = useForm({ defaultValues: { name: "", wheels: "", type: "", model: "", date: "" } });

  const onNext = () => setActiveStep((prev) => prev + 1);
  const onBack = () => setActiveStep((prev) => prev - 1);
  const onSubmit = methods.handleSubmit((data) => console.log(data));

  const StepComponent = StepComponents[activeStep];

  return (
    <FormProvider {...methods}>
      <form onSubmit={onSubmit}>
        <Stepper activeStep={activeStep} className="mb-8">
          {steps.map((label) => (
            <Step key={label}><StepLabel>{label}</StepLabel></Step>
          ))}
        </Stepper>

        <div className="mb-4">
          <StepComponent />
        </div>

        <div className="flex justify-between">
          {activeStep > 0 && <Button onClick={onBack}>Back</Button>}
          {activeStep < steps.length - 1 ? (
            <Button variant="contained" onClick={onNext}>Next</Button>
          ) : (
            <Button variant="contained" type="submit">Submit</Button>
          )}
        </div>
      </form>
    </FormProvider>
  );
};

export default BookingForm;
