import React, { useEffect, useState } from 'react';
import { getVehicleTypes } from '../api';
import {
  Button,
  Typography,
  Box,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormControl,
  CircularProgress
} from '@mui/material';

export default function VehicleTypeStep({ wheels, value, onChange, onNext, onBack }) {
  const [types, setTypes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (wheels) {
      setLoading(true);
      getVehicleTypes(wheels)
        .then(response => {
          setTypes(response.data);
          setLoading(false);
        })
        .catch(err => {
          console.error('Error:', err);
          setError(`Failed to load ${wheels}-wheel vehicle types`);
          setLoading(false);
        });
    } else {
      setLoading(false);
    }
  }, [wheels]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (value) {
      onNext();
    }
  };

  return (
    <Box component="form" onSubmit={handleSubmit} sx={{ p: 2 }}>
      <Typography variant="h5" gutterBottom>
        {wheels 
          ? `Select Vehicle Type (${wheels}-wheel vehicles)`
          : 'Select Vehicle Type'}
      </Typography>

      {error && (
        <Typography color="error" sx={{ mb: 2 }}>
          {error}
        </Typography>
      )}

      {loading ? (
        <Box display="flex" justifyContent="center" sx={{ my: 4 }}>
          <CircularProgress />
        </Box>
      ) : types.length > 0 ? (
        <FormControl component="fieldset" fullWidth>
          <RadioGroup
            name="vehicleType"
            value={value || ''}
            onChange={(e) => onChange(e.target.value)}
          >
            {types.map(type => (
              <FormControlLabel
                key={type.id}
                value={type.id}
                control={<Radio />}
                label={`${type.name}`}
              />
            ))}
          </RadioGroup>
        </FormControl>
      ) : (
        <Typography color="text.secondary">
          {wheels 
            ? `No ${wheels}-wheel vehicle types available`
            : 'Please go back and select number of wheels first'}
        </Typography>
      )}

      <Box mt={4} display="flex" justifyContent="space-between">
        <Button variant="outlined" onClick={onBack}>
          Back
        </Button>
        <Button
          type="submit"
          variant="contained"
          disabled={!value}
        >
          Next
        </Button>
      </Box>
    </Box>
  );
}
