import React, { useEffect, useState } from 'react';
import { getVehicleTypes } from '../api';
import {
  Button,
  Typography,
  Box,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormControl,
  CircularProgress
} from '@material-ui/core';

const VehicleTypeStep = ({ formData, handleChange, nextStep, prevStep }) => {
  const [types, setTypes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchTypes = async () => {
      try {
        const response = await getVehicleTypes();
        const filteredTypes = response.data.filter(type => 
          type.wheelCount === parseInt(formData.wheelCount)
        );
        setTypes(filteredTypes);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching vehicle types:', error);
        setLoading(false);
      }
    };

    if (formData.wheelCount) {
      fetchTypes();
    }
  }, [formData.wheelCount]);

  const handleNext = (e) => {
    e.preventDefault();
    if (formData.vehicleType) {
      nextStep();
    }
  };

  return (
    <Box>
      <Typography variant="h4" gutterBottom>Type of vehicle</Typography>
      <form onSubmit={handleNext}>
        {loading ? (
          <CircularProgress />
        ) : (
          <FormControl component="fieldset">
            <RadioGroup
              aria-label="vehicleType"
              name="vehicleType"
              value={formData.vehicleType}
              onChange={handleChange('vehicleType')}
            >
              {types.map(type => (
                <FormControlLabel 
                  key={type.id} 
                  value={type.id.toString()} 
                  control={<Radio />} 
                  label={type.name} 
                />
              ))}
            </RadioGroup>
          </FormControl>
        )}
        <Box mt={2} display="flex" justifyContent="space-between">
          <Button variant="outlined" onClick={prevStep}>Back</Button>
          <Button 
            type="submit" 
            variant="contained" 
            color="primary" 
            disabled={!formData.vehicleType || loading}
          >
            Next
          </Button>
        </Box>
      </form>
    </Box>
  );
};

export default VehicleTypeStep;
