import React, { useEffect, useState } from 'react';
import { getVehicles } from '../api';
import { Button, Typography, Box, Radio, RadioGroup, FormControlLabel, FormControl, CircularProgress } from '@mui/material';

const VehicleModelForm = ({ formData, handleChange, nextStep, prevStep }) => {
  const [vehicles, setVehicles] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchVehicles = async () => {
      try {
        const response = await getVehicles(formData.vehicleType);
        setVehicles(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching vehicles:', error);
        setLoading(false);
      }
    };

    if (formData.vehicleType) {
      fetchVehicles();
    }
  }, [formData.vehicleType]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (formData.vehicleModel) {
      nextStep();
    }
  };

  return (
    <Box>
      <Typography variant="h4" gutterBottom>Specific Model</Typography>
      <form onSubmit={handleSubmit}>
        {loading ? (
          <CircularProgress />
        ) : (
          <FormControl component="fieldset">
            <RadioGroup
              aria-label="vehicleModel"
              name="vehicleModel"
              value={formData.vehicleModel}
              onChange={handleChange('vehicleModel')}
            >
              {vehicles.map(vehicle => (
                <FormControlLabel 
                  key={vehicle.id} 
                  value={vehicle.id.toString()} 
                  control={<Radio />} 
                  label={vehicle.model} 
                />
              ))}
            </RadioGroup>
          </FormControl>
        )}
        <Box mt={2} display="flex" justifyContent="space-between">
          <Button variant="outlined" onClick={prevStep}>Back</Button>
          <Button 
            type="submit" 
            variant="contained" 
            color="primary" 
            disabled={!formData.vehicleModel || loading}
          >
            Next
          </Button>
        </Box>
      </form>
    </Box>
  );
};

export default VehicleModelForm;
